function processSendMessage(token, authEmail, email, labelID) {
    return new Promise(function(resolve, reject) {
        chrome.runtime.sendMessage({ function: "processEmail", email: email, authEmail: authEmail, token: token, labelID: labelID }, function(response) {
            //var tokenEmail = getParameterByName("access_token",redirectedTo);
            //global.emails.shift();

            //global.checkFlag=0;
            //console.log(response);
            if (response.listID != undefined && response.listID != '')
                global.filters[response.listID] = response.unsubEmail;
            // console.log("filters check");
            // console.log("Adding filter - " + response.listID + " - " + response.unsubEmail);
            //console.log(global.filters);
            resolve();

        });
    });

}

