function replaceAll(str, find, replace) {
    return str.replace(new RegExp(find, 'g'), replace);
}
async function applyFilter(token, authEmail, checkVar, labelID) {
    //console.log("Apply Filter function");
    //console.log(checkVar);
    //console.log(labelID);
    return new Promise(function(resolve, reject) {
        chrome.runtime.sendMessage({ function: "processFilter", query: checkVar, authEmail: authEmail, token: token, labelID: labelID }, function(response) {
            global.tempTotalCount = global.tempTotalCount + response.response;
            jQuery("#totalThreadsSpan").html(tempTotalCount);
            if (global.tempTotalCount != 0) {
                if (global.totalThreads == 0)
                    jQuery("#countDiv").fadeIn();
                jQuery('#totalThreadsSpan').fadeTo(200, 0.3, function() { jQuery(this).fadeTo(500, 1.0); });
            }
            //console.log("Apply filter response" + response.response);
            resolve();
        });
    });

}
