function checkElements(className) {
    return new Promise(function(resolve, reject) {
        var intervalVar = setInterval(function() {
                if (jQuery("." + className).length != 0) {
                    clearInterval(intervalVar);
                    resolve();

                } else {
                    //console.log("Still waiting for" + className);
                }
            },
            1000);
    });
}

function remove_modal_window() {
    jQuery("#gmailJsModalBackground").remove();
    jQuery("#gmailJsModalWindow").remove();
};

function add_modal_window(title, content_html, onClickOk, onClickCancel, onClickClose, okText, cancelText) {
    // By default, clicking on cancel or close should clean up the modal window
    onClickClose = onClickClose || remove_modal_window;
    onClickCancel = onClickCancel || remove_modal_window;


    okText = okText || "OK";
    cancelText = cancelText || "Cancel";

    var background = jQuery(document.createElement("div"));
    background.attr("id", "gmailJsModalBackground");
    background.attr("class", "Kj-JD-Jh");
    background.attr("aria-hidden", "true");
    background.attr("style", "opacity:1;width:100%;height:100%;background-color:rgba(0,0,0,0.15)");

    // Modal window wrapper
    var container = jQuery(document.createElement("div"));
    container.attr("id", "gmailJsModalWindow");
    container.attr("class", "Kj-JD");
    container.attr("tabindex", "0");
    container.attr("role", "alertdialog");
    container.attr("aria-labelledby", "gmailJsModalWindowTitle");
    container.attr("style", "left:50%;top:50%;opacity:1;");

    // Modal window header contents
    var header = jQuery(document.createElement("div"));
    header.attr("class", "Kj-JD-K7 Kj-JD-K7-GIHV4");

    var heading = jQuery(document.createElement("span"));
    heading.attr("id", "gmailJsModalWindowTitle");
    heading.attr("class", "Kj-JD-K7-K0");
    heading.attr("role", "heading");
    heading.html(title);

    var closeButton = jQuery(document.createElement("span"));
    closeButton.attr("id", "gmailJsModalWindowClose");
    closeButton.attr("class", "Kj-JD-K7-Jq");
    closeButton.attr("role", "button");
    closeButton.attr("tabindex", "0");
    closeButton.attr("aria-label", "Close");
    closeButton.click(onClickClose);

    header.append(heading);
    header.append(closeButton);

    // Modal window contents
    var contents = jQuery(document.createElement("div"));
    contents.attr("id", "gmailJsModalWindowContent");
    contents.attr("class", "Kj-JD-Jz");
    contents.html(content_html);

    // Modal window controls
    var controls = jQuery(document.createElement("div"));
    controls.attr("class", "Kj-JD-Jl");

    var okButton = jQuery(document.createElement("button"));
    okButton.attr("id", "gmailJsModalWindowOk");
    okButton.attr("class", "J-at1-auR J-at1-atl");
    okButton.attr("name", "ok");
    okButton.text(okText);
    okButton.click(onClickOk);

    var cancelButton = jQuery(document.createElement("button"));
    cancelButton.attr("id", "gmailJsModalWindowCancel");
    cancelButton.attr("name", "cancel");
    cancelButton.text(cancelText);
    cancelButton.click(onClickCancel);

    controls.append(okButton);
    controls.append(cancelButton);

    container.append(header);
    container.append(contents);
    container.append(controls);

    jQuery(document.body).append(background);
    jQuery(document.body).append(container);

    var center = function() {
        container.css({
            top: (jQuery(window).height() - container.outerHeight()) / 2,
            left: (jQuery(window).width() - container.outerWidth()) / 2
        });
    };

    center();

    container.on("DOMSubtreeModified", center);
    jQuery(window).resize(center);
}

function refresh(f, className) {
    //console.log(className);
    if (jQuery('.' + className + '').length == 0) {
        //console.log("Calling refresh again");
        setTimeout('refresh(' + f + ',"' + className + '")', 100);
    } else {
        f();
    }
}